import React, { useEffect, useState } from "react";
import "./App.css";
import User from "./components/User";
import { CiDark, CiLight } from "react-icons/ci";

const App = () => {
  const [theme, setTheme] = useState("light");

  const toggleTheme = () => {
    if (theme === "light") {
      setTheme("dark");
    } else {
      setTheme("light");
    }
  };

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <div>
      <div style={{}} className={`user__header${theme}`}>
        <h3>devfinder</h3>
        <div className="user__header--content">
          <div onClick={toggleTheme}>
            {theme === "dark" ? (
              <div>
                <p>light</p>
                <CiLight style={{ color: "#fff", fontSize: "24px" }} />
              </div>
            ) : (
              <div>
                <p>Dark</p>
                <CiDark style={{ color: "#ccc", fontSize: "24px" }} />
              </div>
            )}
          </div>
        </div>
      </div>
      <User />
    </div>
  );
};

export default App;

// In this project i use the GitHub users API to pull profile data and display it. It's a great challenge if you're looking to practice working with a 3rd-party API.
